const slider=document.querySelector(".popular-products .slider"),products=slider.querySelectorAll(".catalog-item"),buttonPrev=slider.querySelector(".slider-button-prev"),buttonNext=slider.querySelector(".slider-button-next");let productsTotal=products.length,currentSlideNumber=1;const changeSlide=function(){console.log(currentSlideNumber),products[currentSlideNumber-1].classList.toggle("hidden")};buttonPrev.addEventListener("click",function(){changeSlide(),1===currentSlideNumber?currentSlideNumber=productsTotal:currentSlideNumber--,changeSlide()}),buttonNext.addEventListener("click",function(){changeSlide(),currentSlideNumber===productsTotal?currentSlideNumber=1:currentSlideNumber++,changeSlide()});
